INSERT INTO `24h`.news (id, title, content, createDate, updateDate, avatar, creatorId, editorId, author, categoryId, originalResource, numberAccess, censor) VALUES (8, 'Ngày 13/1: Ghi nhận 16.700 ca COVID-19 tại 63 tỉnh, thành phố', null, null, null, null, null, null, 'Theo Diệu Thu (Tạp chí Du lịch TP.HCM)', null, 'https://tcdulichtphcm.vn/chuyen-dong/ngay-13/1-ghi-nhan-16700-ca-covid-19-tai-63-tinh-thanh-pho-c2a23739.html', 0, null);
